import React, { Component } from 'react'
import './App.css'
import api from './Api'

function imagemPerfil(param){
    return <img src={param} alt="imagem nao pode ser carregada" className="Foto"></img>
}

class App extends Component{
    state = {infoUsuario:[]}

    async componentDidMount(){// realizar uma busca em uma api
        const reposta = await api.get('');// await para garantir o resultado do request

        this.setState({infoUsuario: reposta.data})
    }
    render(){
        const {infoUsuario} = this.state;         

        return(
            <div>
                <h1>Usuario</h1>
                        <h2>
                            <strong>Login: {infoUsuario.login}</strong>

                            <p>
                                <strong>Nome: {infoUsuario.name}</strong>
                            </p>

                            <p>
                                <strong>Foto: </strong>
                                {imagemPerfil(infoUsuario.avatar_url)}
                            </p>
                            {console.log(infoUsuario)} 
                        </h2>
            </div>
        )
    }
    
}

export default App
