import React, { Component } from 'react'
import './App.css'
import api from './Api'
import { render } from '@testing-library/react'

var nome = ""

function imagemPerfil(param) {
    return <img src={param} alt="imagem nao pode ser carregada" className="Foto"></img>
}

// function pesquisar(e) {
//     e.preventDefault();
//     nome = document.getElementById('usuario').value
//     console.log(document.getElementById('usuario').value)
// }

// window.onload = function pesquisar() {
//     document.getElementById('botao').addEventListener("click",function mostrarUsuario() {
//         usuarios = document.getElementById('usuario').value
//         alert(usuarios)
//     })
// }

//e.preventDefault()

class App extends Component {
    state = { infoUsuario: [] } // criar um estado para um array

    async componentDidMount(nome,e) {// realizar uma busca em uma api
        
        console.log("foi chamado")
        const reposta = await api.get(nome)
        this.setState({ infoUsuario: reposta.data })
        e.preventDefault()//para não reiniciar a pagina
        
    }

render() {
    const { infoUsuario } = this.state;   //setando as informações do array     

    return (
        <div className="informacoes">
            <h2>
                {imagemPerfil(infoUsuario.avatar_url)}<br />
                <br />

                <form >
                    <input type="text" id="usuario" />
                    <button type="submit" id="botao" onClick={() => this.componentDidMount(document.getElementById('usuario').value)}>
                        Pesquisar
                        </button>
                </form>

                <br />

                <strong>Login: {infoUsuario.login}</strong><br />
                <br />

                <strong>Nome: {infoUsuario.name}</strong><br />
                <br />

                <strong>Empresa Atual: {infoUsuario.company}</strong><br />
                <br />

                <strong>Localização: {infoUsuario.location}</strong><br />
                <br />

                <strong>Biografia: {infoUsuario.bio}</strong><br />
                <br />

                <strong>Quantidade de repositórios: {infoUsuario.public_repos}</strong><br />
                <br />

                <strong>Quantidade de seguidores: {infoUsuario.followers}</strong><br />
                <br />

                {console.log(infoUsuario)}
            </h2>
        </div>
    )
}
}

export default App
