import React, { Component } from 'react'
import './App.css'
import api from './Api'

class App extends Component{
    state = {filmes:[]}

    async componentDidMount(){
        const reposta = await api.get('');// await para garantir o resultado do request

        this.setState({filmes: reposta.data})
    }
    render(){
        const {filmes} = this.state;         

        return(
            <div>
                <h1>Listar os Filmes</h1>
                {console.log(filmes)}
            </div>
        )
    }
    
}

export default App
