import React, { Component } from 'react'
import './App.css'
import api from './Api'

class App extends Component{
    state = {filmes:[]}

    async componentDidMount(){// realizar uma busca no api
        const reposta = await api.get('batman');// await para garantir o resultado do request

        this.setState({filmes: reposta.data})
    }
    render(){
        const {filmes} = this.state;         

        return(
            <div>
                <h1>Listar os Filmes</h1>
                {filmes.map(filme =>(
                    <li key={filme.show.id}>
                        <h2>
                            <strong>Titulo: </strong>
                            {filme.show.name}
                        </h2>

                        <p>
                         {filme.show.url}
                        </p> 
                    </li>
                ))}
            </div>
        )
    }
    
}

export default App
