import React, { Component } from 'react'
import './App.css'
import api from './Api'

class App extends Component{
    state = {infoUsuario:[]}

    async componentDidMount(){// realizar uma busca em uma api
        const reposta = await api.get('');// await para garantir o resultado do request

        this.setState({infoUsuario: reposta.data})
    }
    render(){
        const {infoUsuario} = this.state;         

        return(
            <div>
                <h1>Listar os usuarios</h1>
                {infoUsuario.map(usuario =>(
                    <li>
                        <h2>
                            <strong>Titulo: </strong>
                            {console.log(infoUsuario)}
                        </h2>
                    </li>
                ))}
            </div>
        )
    }
    
}

export default App
