import React, { Component } from 'react'
import './App.css'
import api from './Api'

function imagemPerfil(param){
    return <img src={param} alt="imagem nao pode ser carregada" className="Foto"></img>
}

class App extends Component{
    state = {infoUsuario:[]} // criar um estado para um array

    async componentDidMount(){// realizar uma busca em uma api
        if(onclick){
            const reposta = await api.get('');// await para garantir o resultado do request
            this.setState({infoUsuario: reposta.data})//atualizo o array
        }
        

    }

    render(){
        const {infoUsuario} = this.state;   //setando as informações do array      

        return(
            <div className="informacoes">
                <h1>Usuario</h1>
                        <h2>
                            <ul>
                                <li><strong>Login: {infoUsuario.login}</strong></li><br />
                                <li><strong>Nome: {infoUsuario.name}</strong></li><br />
                                <li><strong>Foto: {imagemPerfil(infoUsuario.avatar_url)}</strong></li><br />
                                <li><strong>Empresa Atual: {infoUsuario.company}</strong></li><br />
                                <li><strong>Localização: {infoUsuario.location}</strong></li><br />
                                <li><strong>Localização: {infoUsuario.email}</strong></li><br />
                                <li><strong>Biografia: {infoUsuario.bio}</strong></li><br />
                                <li><strong>Quatidade de repositórios: {infoUsuario.public_repos}</strong></li><br />
                                <li><strong>Quatidade de seguidores: {infoUsuario.followers}</strong></li><br />
                            </ul>

                            {console.log(infoUsuario)} 
                        </h2>
            </div>
        )
    }
}

export default App
